              }}
            >
              <CategoryIcon />

              <p className="nav-content dropdown-toggle">Catagories</p>
              <div
              className={show ? "menu-dropdown" : "menu-dropdown no-dropdown"}
            >
              {categoryList?.map((item) => {
                return <div className="dropdown-item">{item}</div>;
              })}
            </div>
            </div>
          </div>
        </div>
        <div className="mg-nav-40"></div>
