import { useState } from "react";
import { CategoryIcon, HomeIcon, SearchIcon } from "./svg";
import "./NavBar.scss";
import { useAllCategories } from "./GetApi";

export const NavBar = () => {
  const [show, setShow] = useState(false);
  const { data: categoryList } = useAllCategories();
  return (
    <>
      <header className="header-section">
        <div className="container-fluid">
          <div className="site-logo">
            <img src="img/logo.png" alt="logo" />
          </div>
          <div className="nav-switch">
            <i className="fa fa-bars"></i>
          </div>
          <div className="header-right">
            <a href="cart.html" className="card-bag">
              <img src="img/icons/bag.png" alt="" />
              <span>2</span>
            </a>
            <a href="#" className="search">
              <img src="img/icons/search.png" alt="" />
            </a>
          </div>
          <ul className="main-menu">
            <li>
              <a href="index.html">Home</a>
            </li>
            <li>
              <a href="index.html">Search</a>
            </li>
            {categoryList?.map((item) => {
              return (
                <li>
                  <a href="#">{item}</a>
                </li>
              );
            })}
          </ul>
        </div>
      </header>

      {/* <div className="nav-seciton">
